mapper.select:
SELECT
FROM t_sys_file_records WHERE del_flag=0
and ( file_name like concat('%',#{searchValue},'%') or type like concat('%',#{searchValue},'%') )
ORDER BY ${orderColumn} ${orderDir}
limit #{start} ,#{length}

update.foreach:
update t_sys_file_records
type= #{item.type}, buss_id = #{item.bussId}, operator = #{item.operator}, status =#{item.status}
where uid = #{item.uid}
