mapper.select:
SELECT
FROM t_kf_bominfo a,t_kf_project_item b WHERE a.del_flag=0 and a.project_id=b.project_id and b.del_flag=0
and a.project_id like concat('%',#{bomInfo.projectId},'%')
and ( a.bom_name like concat('%',#{dataTablesInfo.searchValue},'%') or b.project_name like concat('%',#{dataTablesInfo.searchValue},'%') or a.remark like concat('%',#{dataTablesInfo.searchValue},'%') or b.remark like concat('%',#{dataTablesInfo.searchValue},'%') )
ORDER BY ${dataTablesInfo.orderColumn} ${dataTablesInfo.orderDir}
limit #{dataTablesInfo.start} ,#{dataTablesInfo.length}

mapper.update:
update t_kf_bominfo
set  del_flag=1
where bom_id =#{natrualkey}

update t_kf_fabrics_detail
set  del_flag=1
where bom_id =#{natrualkey}

update t_kf_material_unit_dosage
set  del_flag=1
where material_id =#{natrualkey}

update t_kf_material_spinfo
set  del_flag=1
where material_id =#{natrualkey}

mapper.delete:
update t_kf_bominfo
set  del_flag=1
where project_id =#{projectId}

mapper.select:
select
FROM t_kf_bominfo a,t_jc_series b WHERE a.del_flag=0 and a.series_id=b.series_id and b.del_flag=0 and project_id IN
#{idItem}

mapper.delete:
update t_kf_bominfo set del_flag =1
where bom_id NOT IN

    #{item.natrualkey}

update t_kf_bominfo set del_flag =1
where bom_id IN

    #{item.natrualkey}
