mapper.select:
SELECT

FROM t_kf_bominfo b,t_kf_project_item c
where b.project_id = c.project_id and b.bom_id =#{bomId}

SELECT

FROM t_kf_pre_quote a
where a.bom_id = #{bomId}

SELECT
FROM t_kf_pre_quote a WHERE a.project_item_id IN
#{item}

SELECT
FROM t_kf_pre_quote a,t_jc_customer b WHERE a.del_flag=0 and a.customer_id = b.customer_id
and ( area_name like concat('%',#{searchValue},'%') or area_id like concat('%',#{searchValue},'%') or customer_id like concat('%',#{searchValue},'%') or remark like concat('%',#{searchValue},'%') )
ORDER BY ${orderColumn} ${orderDir}
limit #{start} ,#{length}

mapper.update:
update t_kf_pre_quote
set  del_flag=1
where bom_id =#{bomId}
