mapper.select:
SELECT

FROM t_qx_user
WHERE del_flag=0

    and (
    user_name like concat('%',#{searchValue},'%') or
    user_email like concat('%',#{searchValue},'%')
    )


    ORDER BY ${orderColumn} ${orderDir}

limit #{start} ,#{length}

mapper.update:
update t_qx_user
set  del_flag=1
where user_id =#{natrualkey}

update t_qx_user
set
user_pwd = #{password}
where user_id= #{natrualkey}
