mapper.select:
SELECT
FROM t_jc_category WHERE del_flag=0
and ( category_id like concat('%',#{searchValue},'%') or category_name like concat('%',#{searchValue},'%') or level_id like concat('%',#{searchValue},'%') or remark like concat('%',#{searchValue},'%') )
ORDER BY ${orderColumn} ${orderDir}
limit #{start} ,#{length}

mapper.update:
update t_jc_category 	set
category_name=#{name},
level_id = #{levelId},
parent_category_id=#{parentCategoryId},
remark=#{remark}
WHERE category_id =#{natrualkey}

update t_jc_category
set  del_flag=1
where category_id =#{natrualkey}

mapper.select:
select
FROM t_jc_category WHERE del_flag=0
and parent_category_id =#{categoryId}
