mapper.select:
SELECT

FROM t_wl_finish
WHERE del_flag=0

    and (
    finish_name like concat('%',#{searchValue},'%') or
    remark like concat('%',#{searchValue},'%')
    )


    ORDER BY ${orderColumn} ${orderDir}


    ORDER BY update_time desc

limit #{start} ,#{length}

mapper.update:
update t_wl_finish 	set
finish_name=#{name},
remark=#{remark}
WHERE finish_id =#{natrualkey}

update t_wl_finish
set  del_flag=1
where finish_id =#{natrualkey}
