mapper.select:
SELECT
FROM t_wl_material_classic a,t_wl_material_type b WHERE a.material_type_id = b.material_type_id and a.del_flag=0
and ( a.classic_name like concat('%',#{searchValue},'%') or b.material_type_id like concat('%',#{searchValue},'%') or a.remark like concat('%',#{searchValue},'%') )
ORDER BY a.${orderColumn} ${orderDir}
ORDER BY a.update_time desc
limit #{start} ,#{length}

mapper.update:
update t_wl_material_classic 	set
classic_name=#{name},
material_type_id=#{materialTypeId},
remark=#{remark}
WHERE classic_id =#{natrualkey}

update t_wl_material_classic
set  del_flag=1
where classic_id =#{natrualkey}
