mapper.select:
SELECT
FROM t_wl_material_type WHERE del_flag=0
and ( material_type_name like concat('%',#{searchValue},'%') or material_type_code like concat('%',#{searchValue},'%') or remark like concat('%',#{searchValue},'%') )
ORDER BY ${orderColumn} ${orderDir}
ORDER BY update_time desc
limit #{start} ,#{length}

mapper.update:
update t_wl_material_type
set  del_flag=1
where material_type_id =#{natrualkey}
