mapper.select:
SELECT

FROM t_kf_packaging
WHERE del_flag=0

    and (
    packaging_name like concat('%',#{searchValue},'%') or
    sp_id like concat('%',#{searchValue},'%') or
    remark like concat('%',#{searchValue},'%')
    )


    ORDER BY ${orderColumn} ${orderDir}

limit #{start} ,#{length}

mapper.update:
update t_kf_packaging
set  del_flag=1
where packaging_id =#{natrualkey}

mapper.delete:
DELETE from t_kf_packaging
where packaging_id IN

    #{idItem}

mapper.update:
update t_kf_material_unit_dosage
set  del_flag=1
where material_id =#{natrualkey}

update t_kf_material_spinfo
set  del_flag=1
where packaging_id =#{natrualkey}
